  <script>
    $(window).ready(function () {
      $('body').addClass('rsFooter');
      $('.btn-donation').on('click', function () {
        var that = $(this)
        $('.btn-donation.selected').removeClass('selected')
        $('.container-open-input input').removeClass('selected')
        that.addClass('selected');
        $('span.before-price').html(that.attr('value'))
        $('span.after-price').html(Math.ceil((that.attr('value') * 25) / 100))
      })
      $('.container-open-input input').on('keypress', function (e) {
        e.preventDefault()
        $(this).addClass('selected')
        $('.btn-donation.selected').removeClass('selected')
        var value = $(this).val()
        var key = (event.keyCode ? event.keyCode : event.which);
        var ch = String.fromCharCode(key)
        if (ch == '-') {
          $(this).val('1')
          $('span.before-price').html(1)
          $('span.after-price').html(1)
        } else {
          var total = Number(value + ch)
          $(this).val(total)
          $('span.before-price').html(total)
          if (total < 538) {
            $('span.after-price').html(Math.ceil((total * 25) / 100))
          } else {
            $('span.after-price').html(Math.ceil((537 * 25) / 100 + ((total - 537) * 34) / 100))
          }
        }
      })
      $('.goto-donate').on('click', function (e) {
        e.preventDefault()
        var that = $(this)
        var value = $('.donation-bloc .selected').attr('value') || $('.donation-bloc .selected').val()
        window.location = that.attr('href') + '?value=' + value + '#donation-bloc'
      })
    });
  </script>

